import AGGrid from "@/components/AGGrid.vue";
import infoCellRenderer from "@/components/infoCellRenderer";
import AG_GRID_LOCALE_RU from "@/components/locale.ru.js";
export default {
  mounted() {
    this.fetchJson();
    this.ruLocale = AG_GRID_LOCALE_RU;
  },
  components: {
    AGGrid,
    infoCellRenderer
  },
  data() {
    return {
      isLoading: true,
      gridData: [],
      columnDefs: [{
        headerName: "",
        field: "tkey",
        cellRenderer: "infoCellRenderer",
        width: 70
      }, {
        headerName: "№",
        valueGetter: params => {
          return params.node ? params.node.rowIndex + 1 : null;
        },
        width: 60
      }, {
        headerName: "ФИО",
        field: "name",
        width: 280,
        filter: true,
        floatingFilter: true
      }, {
        headerName: "Должность/Место работы",
        field: "infoWorkPlaces",
        width: 280,
        filter: true,
        floatingFilter: true
      }, {
        headerName: "Ставка",
        valueGetter: params => {
          return params.data.stake;
        },
        valueSetter: params => {
          let url = 'http://212.193.88.196:8080/changestake?tkey=' + params.data.tkey + '&value=' + params.newValue;
          fetch(url);
          params.data.stake = params.newValue;
          return true;
        },
        editable: true,
        width: 100
      }, {
        headerName: "Часы на ставку",
        valueGetter: params => {
          return Math.round(params.data.hoursOnStake, -2);
        },
        valueSetter: params => {
          let url = 'http://212.193.88.196:8080/changehours?guidPerson1C=' + params.data.guidPerson1C + '&value=' + params.newValue;
          fetch(url);
          params.data.hoursOnStake = params.newValue;
          params.data.hours = params.data.bHoursPlaned - params.data.hoursOnStake;
          return true;
        },
        editable: true,
        width: 100
      }, {
        headerName: "Кол-во часов на почасовую оплату",
        valueGetter: params => {
          return Math.round(params.data.hours, -2);
        },
        valueSetter: params => {
          params.data.hours = params.newValue;
          return true;
        },
        width: 130
      }, {
        headerName: 'Бюджет',
        children: [{
          headerName: "Планируемое кол-во часов",
          field: "bHoursPlaned",
          width: 140
        }, {
          headerName: "Фактическое кол-во часов",
          field: "bHoursReal",
          editable: true,
          width: 140
        }, {
          headerName: "Разница",
          field: "bHoursDiff",
          width: 100
        }]
      }, {
        headerName: 'Контракт',
        children: [{
          headerName: "Планируемое кол-во часов",
          field: "cHoursPlaned",
          width: 140
        }, {
          headerName: "Фактическое кол-во часов",
          field: "cHoursReal",
          width: 140
        }, {
          headerName: "Разница",
          field: "cHoursDiff",
          width: 100
        }]
      }, {
        headerName: 'Общее',
        children: [{
          headerName: "Планируемое кол-во часов",
          field: "hoursPlaned",
          width: 140
        }, {
          headerName: "Фактическое кол-во часов",
          field: "hoursReal",
          width: 140
        }, {
          headerName: "Разница",
          field: "hoursDiff",
          width: 100
        }]
      }, {
        headerName: "Год",
        field: "year",
        width: 130,
        filter: true,
        floatingFilter: true
      }],
      defaultColDef: {
        sortable: true,
        resizable: true,
        spanHeaderHeight: true,
        wrapHeaderText: true,
        minWidth: 50,
        wrapText: true,
        autoHeight: true,
        autoHeaderHeight: true
      }
    };
  },
  methods: {
    async fetchJson() {
      let json;
      await fetch("http://212.193.88.196:8080/loaddata").then(resp => resp.text()).then(razm => json = razm);
      this.gridData = await JSON.parse(json);
      this.isLoading = false;
    }
  }
};
